import React, { useState } from 'react';
import DishCard from './DishCard';
import mainDishesData from '../data/mainDishesData';
import './MainDishes.css';

const MainDishes = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [dishesPerPage] = useState(9); // Change to 9 dishes per page

  const indexOfLastDish = currentPage * dishesPerPage;
  const indexOfFirstDish = indexOfLastDish - dishesPerPage;
  const currentDishes = mainDishesData.slice(indexOfFirstDish, indexOfLastDish);

  const totalPages = Math.ceil(mainDishesData.length / dishesPerPage);

  const handleClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <div className="dish-list">
        {currentDishes.map((dish) => (
          <DishCard key={dish.id} dish={dish} />
        ))}
      </div>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => (
          <div
            key={i + 1}
            onClick={() => handleClick(i + 1)}
            className={`page-number${currentPage === i + 1 ? ' active' : ''}`}
          >
            {i + 1}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MainDishes;
