import React,{useState} from 'react'
//import Slides from './Slides';
//import SlideData from './SlideData';

import Photo1 from '../images/網站照片/主菜照片/三杯雞肉.jpg';
import Photo2 from '../images/網站照片/主菜照片/宮保雞丁.jpg';
import Photo3 from '../images/網站照片/主菜照片/泰式雞排.jpg';
import Photo4 from '../images/網站照片/主菜照片/滷排骨.jpg';
import Photo5 from '../images/網站照片/主菜照片/炸排骨.jpg';
import Photo6 from '../images/網站照片/主菜照片/炸雞排.jpg';
import Photo7 from '../images/網站照片/主菜照片/炸雞腿.jpg';
import Photo8 from '../images/網站照片/主菜照片/煎鱈魚.jpg';
import Photo9 from '../images/網站照片/主菜照片/爌肉.jpg';
import Photo10 from '../images/網站照片/主菜照片/蔥爆牛肉.jpg';

import './CardCarousel.css';
import {Carousel} from '3d-react-carousal';
function CardCarousel() {
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    let slides = [
        <img src={Photo1} alt="slide1" className="sliderimg"/>,
        <img src={Photo2} alt="slide2" className="sliderimg"/>,
        <img src={Photo3} alt="slide3" className="sliderimg"/>,
        <img src={Photo4} alt="slide4" className="sliderimg"/>,
        <img src={Photo5} alt="slide5" className="sliderimg"/>,
        <img src={Photo6} alt="slide6" className="sliderimg"/>,
        <img src={Photo7} alt="slide7" className="sliderimg"/>,
        <img src={Photo8} alt="slide8" className="sliderimg"/>,
        <img src={Photo9} alt="slide9" className="sliderimg"/>,
        <img src={Photo10} alt="slide10" className="sliderimg"/>
    ]

    return (
        <div className="carousel-outer">
            <h2 className="slider-header">媒體報導</h2>
            <Carousel slides={slides}  autoplay={true} interval={3000}/>
        </div>
    )   
}

export default CardCarousel;
