import Photo3 from '../images/網站照片/主菜照片/泰式雞排.jpg';
import Photo4 from '../images/網站照片/主菜照片/滷排骨.jpg';
import './ImageSlider.css';

//import ReactCardSlider from 'react-card-slider-component';
