import './CardCarousel.css'
import {SliderData} from './SliderData';
import {FaArrowAltCircleLeft,FaArrowAltCircleRight} from 'react-icons/fa'

const CardCarousel = ({slides}) => {
    const [current,setCurrent] = useState(0)
    const length = slides.length
    return (
        <section className="slider">
            <FaArrowAltCircleLeft className="left-arrow"></FaArrowAltCircleLeft>
            <FaArrowAltCircleRight className="right-arrow"></FaArrowAltCircleRight>
            {SliderData.map((slide,index)=>{
                return <img src={slide.image} alt="food image" className="slide-image"/>;
            })}
        </section>
    )
}


export default CardCarousel;
