import React, { useState } from 'react';
import { Box, Card, CardContent, CardActions, CardMedia, Typography, Button, Grid } from '@mui/material';

const dishes = [
  { id: 1, name: 'Dish 1', imageUrl: 'https://via.placeholder.com/200' },
  { id: 2, name: 'Dish 2', imageUrl: 'https://via.placeholder.com/200' },
  { id: 3, name: 'Dish 3', imageUrl: 'https://via.placeholder.com/200' },
  { id: 4, name: 'Dish 4', imageUrl: 'https://via.placeholder.com/200' },
  { id: 1, name: 'Dish 1', imageUrl: 'https://via.placeholder.com/200' },
  { id: 2, name: 'Dish 2', imageUrl: 'https://via.placeholder.com/200' },
  { id: 3, name: 'Dish 3', imageUrl: 'https://via.placeholder.com/200' },
  { id: 4, name: 'Dish 4', imageUrl: 'https://via.placeholder.com/200' },
  // Add more dishes as you like
];

function DishGrid() {
  const [backendData, setBackendData] = useState([{}]);

  const [posts,setPosts] = useState([]);
  const [selectedDish, setSelectedDish] = useState(null);

  useEffect(()=>{
    fetch("/api/rawdata").then(
        response=> response.json()
    ).then(
        data => {
            setBackendData(data)
        }
    )
},[])
  const handleSelectDish = (dish) => {
    setSelectedDish(dish);
  };

  return (
    <Box sx={{
        flexGrow: 1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        m: 2, // Add margin to the box, you can change this value as needed
      }}>
      <Grid container spacing={6}>
        {dishes.map((dish) => (
          <Grid key={dish.id} item xs={12} sm={6} md={4}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={dish.imageUrl}
                alt={dish.name}
                sx={{
                    objectFit: 'cover',
                    borderTopLeftRadius: 2,
                    borderTopRightRadius: 2,
                  }}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {dish.name}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small" onClick={() => handleSelectDish(dish)}>
                  Choose
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      {selectedDish && (
        <Typography variant="h6" component="p">
          You have selected {selectedDish.name}.

        </Typography>
      )}
    </Box>
  );
}

export default DishGrid;
