import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const styles = {
  cardContainer: {
    padding: '15px',
  },
  card: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    borderRadius: '5px',
    overflow: 'hidden',
    transition: 'all 0.3s ease',
  },
  cardImage: {
    width: '100%',
    height: 'auto',
    objectFit: 'cover',
  },
  cardBody: {
    padding: '15px',
    textAlign: 'center',
  },
  cardTitle: {
    margin: '0 0 10px',
    fontSize: '1.25rem',
  },
  cardDescription: {
    fontSize: '1rem',
    lineHeight: '1.5',
  },
};

const CardCarousel = ({ cards }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <Slider {...settings}>
      {cards.map((card, index) => (
        <div key={index} style={styles.cardContainer}>
          <div style={styles.card}>
            <img src={card.image} alt={card.title} style={styles.cardImage} />
            <div style={styles.cardBody}>
              <h3 style={styles.cardTitle}>{card.title}</h3>
              <p style={styles.cardDescription}>{card.description}</p>
            </div>
          </div>
        </div>
      ))}
    </Slider>
  );
};

export default CardCarousel;
