import React,{useState} from 'react'
import { Box, Card, CardContent, CardActions, CardMedia, Typography, Button, Grid } from '@mui/material';
const dishes = [
    { id: 1, name: 'Dish 1', imageUrl: 'https://via.placeholder.com/200' },
    { id: 2, name: 'Dish 2', imageUrl: 'https://via.placeholder.com/200' },
    { id: 3, name: 'Dish 3', imageUrl: 'https://via.placeholder.com/200' },
    // Add more dishes as you like
  ];
const MainDish = () => {
  return (
    <Card>
    <CardMedia
      component="img"
      height="200"
      image={dish.imageUrl}
      alt={dish.name}
    />
    <CardContent>
      <Typography gutterBottom variant="h5" component="div">
        {dish.name}
      </Typography>
    </CardContent>
    <CardActions>
      <Button size="small" onClick={() => onSelect(dish)}>
        Choose
      </Button>
    </CardActions>
  </Card>
  )
}

export default MainDish
