import React, { useEffect, useState } from "react";
import axios from "axios";

import "./MainDish.css";
//import CryptoList from "./components/CryptoList";
import MainDishList from "./MainDishList";
import Pagination from "./Pagination";

function MainDish(){
    const [dishesData,setDishesData] = useState([]);
    const [currentPage,setCurrentPage] = useState(1);
    const [dishesPerPage,setDishesPerPage] = useState(9);

    const [backendData, setBackendData] = useState([{}]);

    useEffect(()=>{
        fetch("/api/").then(
            response=> response.json()
        ).then(
            data => {
                setBackendData(data)
            }
        )
    },[])
    const lastDishIndex = currentPage * dishesPerPage;
    const firstDishIndex = lastDishIndex - dishesPerPage;
    const currentDishes = dishesData.slice(firstDishIndex, lastDishIndex);
    return (
        <div className='dish-container'>
            <h1>Dish to Select</h1>
            <h2> testing</h2>
            {currentDishes}
            <MainDishList dishesData={currentDishes} />
            hello
            <Pagination
                totalDishes={dishesData.length}
                dishesPerPage={dishesPerPage}
                setCurrentPage={setCurrentPage}
                currentPage={currentPage}
            />
        </div>
    );

}


export default MainDish;
