// src/components/MainDishes.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Pagination } from 'react-bootstrap';
import DishCard from './DishCard';
import mainDishesData from './mainDishesData';

const MainDishes = () => {
  const [dishes, setDishes] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [dishesPerPage] = useState(9);
  const [selectedDishes, setSelectedDishes] = useState([]);

  useEffect(() => {
    // Fetch data from the API or use the sample data
    setDishes(mainDishesData);
  }, []);

  // Logic for displaying dishes based on pagination
  const indexOfLastDish = currentPage * dishesPerPage;
  const indexOfFirstDish = indexOfLastDish - dishesPerPage;
  const currentDishes = dishes.slice(indexOfFirstDish, indexOfLastDish);

  // Update the current page
  const handlePageClick = (event, pageNumber) => {
    event.preventDefault();
    setCurrentPage(pageNumber);
  };

  const totalPages = Math.ceil(dishes.length / dishesPerPage);

  const handleDishSelection = (selectedDish) => {
    if (selectedDishes.some((dish) => dish.id === selectedDish.id)) {
      setSelectedDishes(selectedDishes.filter((dish) => dish.id !== selectedDish.id));
    } else {
      setSelectedDishes([...selectedDishes, selectedDish]);
    }
  };

  return (
    <Container>
      <Row>
        {currentDishes.map((dish) => (
          <Col key={dish.id} md={4} className="mb-4">
            <DishCard dish={dish} onDishSelect={handleDishSelection} />
          </Col>
        ))}
      </Row>
      <Row>
        <Col>
          <Pagination className="justify-content-center">
            {[...Array(totalPages)].map((_, i) => (
              <Pagination.Item
                key={i + 1}
                active={i + 1 === currentPage}
                onClick={(e) => handlePageClick(e, i + 1)}
              >
                {i + 1}
              </Pagination.Item>
            ))}
          </Pagination>
        </Col>
      </Row>
    </Container>
  );
};

export default MainDishes;
